    searchArtist = () => {
        alert("in function");
        let artist_name = this.refs.artist_name.value;
        //alert(artist_name)
        alert('https://itunes.apple.com/search?term='+artist_name)
        fetch('https://itunes.apple.com/search?term='+artist_name).then((resp)=>{
            resp.json().then((result)=>{
                console.log(result.data)
                this.setState({artist:result.data})
            })
        })
    }
    render()
    {
        return(
        <div className="container">
            <div>
                <form onSubmit={this.searchArtist}>
                    <table>
                        <tr>
                            <td>Artist Name</td>
                            <td><input type="text" ref="artist_name" name="artist_name"/></td>
                        </tr>
                        <tr>
                            <td><input type="submit" value="Submit"/></td>
                        </tr>
                    </table>
                </form>
            </div>
          
        </div>
        )
    }
}
export default SearchArtist;
