import React from 'react';

class SearchArtist extends React.Component
{
    constructor(props)
    {
        super(props);
        this.artist_name = React.createRef();
        this.state = { 
            aname : " ",
            artist : [],
            
         }
         this.artistOnChange = this.artistOnChange.bind(this);
    }

/*componentDidMount()
    {
        //let artist_name = this.artist_name.current.value;
        alert(this.state.aname)
       // console.log(artist_name);
        //alert('https://itunes.apple.com/search?term='+artist_name)
        fetch('https://itunes.apple.com/search?term='+this.state.aname+'&limit=5')
        .then(response =>
            response.json())
        
        .then((data) =>{
            alert(JSON.stringify(data));
          this.setState({
            artist: data.results
            
          });
        })
  
    }*/
    artistOnChange(e)
    {
        this.setState({aname:e.target.value});
    }
   searchArtist(value) {
        
        alert(this.state.aname);
        fetch('https://itunes.apple.com/search?term='+value+'&limit=5')
        .then(response =>
            response.json())
        
        .then((data) =>{
            alert(JSON.stringify(data));
          this.setState({
            artist: data.results
            
          });
        })
    }
    handleSubmit(e){
        this.setState({aname:e.target.value});
        e.preventdefault();
        searchArtist(e.target.value);
    }
    render()
    {
        //const artist = this.state.artist;
        return(
        <div className="container">
            <div>
                <form onSubmit={this.handleSubmit}>
                    <table>
                        <tbody>
                            <tr>
                                <td>Artist Name</td>
                                <td><input type="text" value={this.state.aname} name="artist_name" onChange={this.artistOnChange}/></td>
                            </tr>
                            <tr>
                                <td><input type="submit" value="Submit"/></td>
                            </tr>
                        </tbody>
                    </table>
                </form>
                <div>
               
                <table>
                    <thead>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                    </tr>
                    </thead>
                </table>
                {this.state.artist.map((a) =>{
                    return(
                    <div>
                        <div>
                            <table>
                                <tbody>
                                <tr>
                                    <td>{a.artistId}</td>
                                    <td>{a.artistName}</td>
                                </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>
                 ) })}
                </div>
            </div>
          
        </div>
        )
    }
}
export default SearchArtist;
