import React from 'react';

class SearchArtist extends React.Component
{
    constructor(props)
    {
        super(props);
        this.artist_name = React.createRef();
        this.state = { 
            aname : " ",
            artist : []
         }
         this.artistOnChange = this.artistOnChange.bind(this);
    }

   /*componentDidMount()
    {
        let artist_name = this.artist_name.current.value;
        alert(artist_name)
        console.log(artist_name);
        alert('https://itunes.apple.com/search?term='+artist_name)
        fetch('https://itunes.apple.com/search?term='+artist_name)
        .then(response =>
            response.json())
        
        .then((data) =>{
            alert(JSON.stringify(data));
          this.setState({
            artist: data,
            
          });
        })
  
    }*/
    artistOnChange(e)
    {
        this.setState({aname:e.target.value});
    }
    searchArtist = (e) => {
        
        alert("in function");
        //let artist_name = this.artist_name.current.value;
        alert(this.state.aname)
        console.log(this.state.aname);
        //document.write(this.state.aname)
        alert('https://itunes.apple.com/search?term='+this.state.aname)
        fetch('https://itunes.apple.com/search?limit=1&term='+this.state.aname)
        .then((response) =>
            response.json())
        
        .then((result) =>{
            alert(JSON.stringify(result.results[0]));
          this.setState({

            artist: result,
            
          });
        })
        document.write(this.state.artist);
       
    }
    render()
    {
        const {artist} = this.state;
        return(
        <div className="container">
            <div>
                <form onSubmit={this.searchArtist}>
                    <table>
                        <tbody>
                            <tr>
                                <td>Artist Name</td>
                                <td><input type="text" value={this.state.aname} name="artist_name" onChange={this.artistOnChange}/></td>
                            </tr>
                            <tr>
                                <td><input type="submit" value="Submit"/></td>
                            </tr>
                        </tbody>
                    </table>
                </form>
                <div>
                <table  className="table table-hover table-bordered">
            <thead>
              <tr>
                <th>Id</th>
                <th> Name</th>
               
              </tr>
            </thead>
            <tbody>
              
              { this.state.artist.map(a=> (
                <tr key={a.results.artistId}>
                  <td>{a.results.artistId}</td>
                  <td>{a.results.artistName}</td>
                  
                </tr>
              ))}
            </tbody>
          </table>
                </div>
            </div>
          
        </div>
        )
    }
}
export default SearchArtist;
